import { createStore } from "redux";
import { createSlice } from "@reduxjs/toolkit";

// section for actions
export const INCREMENT = "increment";
export const DECREMENT = "decrement";
export const INCREASE = "increase";
export const TOGGLE = "toggle";

// reducer
const initialState = {
  counter: 0,
  showCounter: true,
};

// create slice has an internal IMER tool that allows us to seem like we are mutating state. But internally
// its creating immutable objects for us
createSlice({
  name: "counter",
  initialState: initialState,
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter + action.amount;
    },
    toggleCounter(state) {},
  },
});

// WE ALWAYS HAVE TO RETURN NEW INSTANCE OF STATE. WE CANNOT MERGE WITH EXISTING STATE. WE CANNOT MUTATE EXISTING STATE.
// WE ALWAYS RETURN NEW INTSANCE ALWAYS!!!
const counterReducer = (state = initialState, action) => {
  if (action.type === INCREMENT) {
    return {
      counter: state.counter + 1,
      showCounter: state.showCounter,
    };
  }

  if (action.type === DECREMENT) {
    return {
      showCounter: state.showCounter,
      counter: state.counter - 1,
    };
  }

  if (action.type === INCREASE) {
    return {
      counter: state.counter + action.amount,
      showCounter: state.showCounter,
    };
  }

  if (action.type === TOGGLE) {
    return {
      counter: state.counter,
      showCounter: !state.showCounter,
    };
  }

  // return unchanged state
  return state;
};

// creating store
const store = createStore(counterReducer);

// export to allow connecting to it
export default store;
