import { createSlice, configureStore } from "@reduxjs/toolkit";

// reducer
const initialCounterState = {
  counter: 0,
  showCounter: true,
};

// create slice has an internal IMER tool that allows us to seem like we are mutating state. But internally
// its creating immutable objects for us
const counterSlice = createSlice({
  name: "counter",
  initialState: initialCounterState,
  reducers: {
    increment(state) {
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    },
    increase(state, action) {
      state.counter = state.counter + action.payload;
    },
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

// AUTH store
const authInitialState = {
  isAuthenticated: false,
};
const authSlice = createSlice({
  name: "auth",
  initialState: authInitialState,
  reducers: {
    login(state) {
      state.isAuthenticated = true;
    },
    logout(state) {
      state.isAuthenticated = false;
    },
  },
});

// When we have multiple slices we will run into issues. Since we can only pass single slice to createStore()
// so we instead use configureStore
/*
  configureStore({
    reducer: {
      counter: counterSlice.reducer,
      ...
    }
  })
 */
// creating store
const store = configureStore({
  reducer: counterSlice.reducer,
});

export const counterActions = counterSlice.actions;

// export to allow connecting to it
export default store;
