import Header from "./components/Header/Header";
import Investments from "./components/Investments/Investments";
import NewInvestmentForm from "./components/NewInvesmentForm/NewInvestmentForm";
import {useState} from "react";

function App() {
  // states
  const [investments, setInvestments] = useState([]);

  const calculateHandler = (userInput) => {
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    const yearlyData = []; // per-year results

    let currentSavings = +userInput.currentSavings; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput.yearlyContributions; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput.expectedReturn / 100;
    const duration = +userInput.duration;

    let USDollar = new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    });
    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {

      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      /*yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: USDollar.format(yearlyInterest),
        savingsEndOfYear: USDollar.format(currentSavings),
        yearlyContribution: USDollar.format(yearlyContribution),
        investedCapital: USDollar.format(currentSavings - yearlyInterest)
      });*/

      // new format
      yearlyData.push({
        year: i + 1,
        totalSavings: currentSavings,
        yearlyInterest: yearlyInterest,
        totalInterest: yearlyInterest + yearlyData[i-1].yearlyInterest,
        investedCapital: currentSavings - yearlyInterest
      })
    }

    // do something with yearlyData ...
    setInvestments(yearlyData);
  };

  return (
    <div>
      <Header/>
      <NewInvestmentForm onAddNewInvestment={calculateHandler}/>
      <Investments investments={investments}/>
    </div>
  );
}

export default App;
