import styles from './AddUser.module.css';
import Button from "../UI/Button/Button";
import {useState, useRef} from "react";
import Card from "../UI/Card/Card";
import ErrorModal from "../UI/ErrorModal/ErrorModal";

const AddUser = props => {
    // state
    const [userInput, setUserInput] = useState({
        username: '',
        age: '',
        id: null
    });
    const [error, setError] = useState();

    // events
    const submitHandler = (event) => {
        event.preventDefault();
        // checks
        if (userInput.age.trim().length === 0 || userInput.username.trim().length === 0) {
            setError({
                title: 'Invalid Input',
                message: 'Please enter valid name and age (non-empty values).'
            })
            return
        }
        if (+userInput.age < 0) {
            setError({
                title: 'Invalid Input',
                message: 'Please enter valid age (> 0).'
            })
            return
        }
        props.onAddUser(userInput);
        // clear inputs
        setUserInput({
            id: null,
            username: '',
            age: ''
        })
    }

    const errorHandler = () => {
        setError(null);
    }

    const inputHandler = (input, value) => {
        setUserInput((prevInput) => {
            return {...prevInput, [input]:value, id: Math.random().toString()}
        });
    }

    return <>
        {error && <ErrorModal title={error.title} message={error.message} onConfirm={errorHandler}/>}
        <Card>
            <form className={styles['form-container']}>
                <div className={styles['input-group']}>
                    <label htmlFor='username'>Username</label>
                    <input type='text' id='username' value={userInput.username} onChange={(event) => {inputHandler('username', event.target.value)}}/>
                </div>
                <div className={styles['input-group']}>
                    <label htmlFor='age'>Age (Years)</label>
                    <input type='number' step='1' id='age' value={userInput.age} onChange={(event) => {inputHandler('age', event.target.value)}}/>
                </div>
            <Button type='submit' onClick={submitHandler}>Add User</Button>
            </form>
        </Card>
    </>
}

export default AddUser;
