import styles from './AddUser.module.css';
import Button from "../Button/Button";
import {useState} from "react";

const AddUser = props => {
    // state
    const [userInput, setUserInput] = useState({
        username: '',
        age: '',
        id: null
    });

    // events
    const submitHandler = () => {
        // checks
        if (userInput.age.trim().length === 0 || userInput.username.trim().length === 0) console.log('Username and age cannot be empty')
        if (+userInput.age < 0) console.log('age cannot be less than 0');
        props.onAddUser(userInput);
    }

    const inputHandler = (input, value) => {
        setUserInput((prevInput) => {
            return {...prevInput, [input]:value, id: Math.random().toString()}
        });
    }

    return <form className={styles['form-container']}>
            <div className={styles['input-group']}>
                <label htmlFor='username'>Username</label>
                <input type='text' id='username' value={userInput.username} onChange={(event) => {inputHandler('username', event.target.value)}}/>
            </div>
            <div className={styles['input-group']}>
                <label htmlFor='age'>Age (Years)</label>
                <input type='number' step='1' id='age' value={userInput.age} onChange={(event) => {inputHandler('age', event.target.value)}}/>
            </div>
        <Button type='submit' onAddUser={submitHandler}>Add User</Button>
        </form>
}

export default AddUser;
