import React, {useState} from "react";
import "./NewExpense.css"
import ExpenseForm from "./ExpenseForm";
import Button from "../UI/Button";

const NewExpense = (props) => {
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        };

        // send the data up to parent
        props.onAddExpense(expenseData);
    };

    const [addNewExpenseActive, setAddNewExpenseActive] = useState(false);

    const addNewExpenseButtonHandler = () => {
        setAddNewExpenseActive((prevState) => prevState ? false : true)
    }

    return <div className='new-expense'>
        {addNewExpenseActive ? <ExpenseForm onSaveExpenseData={saveExpenseDataHandler}></ExpenseForm> : <Button onClickAddExpense={addNewExpenseButtonHandler} class='active'>Add new expense</Button> }
    </div>
}

export default NewExpense;
