import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import {useState} from "react";

const Expenses = ({items}) => {
    const [filteredYear, setFilteredYear] = useState('2020');
    let filterInfoText = '2019, 2021 & 2022';
    if (selectedYear === '2019') filterInfoText = '2020, 2021 & 2022'
    else if(selectedYear === '2020') filterInfoText = '2019, 2021 & 2022';
    else if(selectedYear === '2021') filterInfoText = '2019, 2020 & 2022';
    else setFilterInfoText('2019,2020 & 2021')

    const filterChangeHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
    }

    return (
        <Card className='expenses'>
            <ExpensesFilter onChangeFilter={filterChangeHandler} selected={filteredYear}/>
            <p>Data for years {filterInfoText} is hidden</p>
            {items.map((item) => <ExpenseItem title={item.title} amount={item.amount} date={item.date}/>)}
        </Card>
    );
}

export default Expenses;
